/*	--------------------------------------------------------------------------------
	QPushButton - BOTÓN
	-------------------------------------------------------------------------------- */

QPushButton {
	background-color: transparent;
	border: 0px solid #E0E0E0;
	border-radius: 0px;
	color: #212121;
	font-size: 12px;
	height: 32px;
	line-height: 32px;
	text-align: center;
	qproperty-iconSize: 32px; }

QPushButton#BTN_USR {
	image:url(c:/img/usr_ico.png); }

QPushButton:hover {
	border: 1px solid #3F51B5; 
                    height: 62px;}

QPushButton:focus {
	border: 2px solid #3F51B5; }

QPushButton:pressed {
	border: 2px solid #3F51B5; }

QPushButton:disabled {
	background-color: #9E9E9E;
	border: 1px solid #9E9E9E;
	color:  #FFF; }

QPushButton::menu-indicator {
	image: none;
	width: 0px; }




/*	--------------------------------------------------------------------------------
	QTooltip - TOOLTIP
	-------------------------------------------------------------------------------- */

QToolTip {
	align: center;
	background-color: #FFFFFF;
	border: 1px solid #727272;
	color: #333333;
	vertical-align:center; }

/*	--------------------------------------------------------------------------------
	QMenuBar - BARRA DE MENU (Solo afecta a Windows)
	-------------------------------------------------------------------------------- */

QMenuBar {
	background-color: #FFF;
	border-bottom: 1px solid #CECECE;
	min-height:0px; }

QMenuBar::item:selected {
	background-color: #CECECE;
	color: #000; }

QMenuBar::item:hover { 
	background: #333333;
	color: #FFF; }

/*	--------------------------------------------------------------------------------
	QDockWidget - DOCK
	-------------------------------------------------------------------------------- */

QDockWidget {
	color: #727272;
	font-size: 10px;
	min-width:10px;
	width: 210px; }

QDockWidget::title {
	background: #CECECE;
	margin: 0px;
	padding: 0px;
	text-align: center; }

QDockWidget::close-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px; left: 0px; bottom: 0px;
	width: 0px; }

QDockWidget::float-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px; left: 0px; bottom: 0px;
	width: 0px; }

/*	--------------------------------------------------------------------------------
	QMainWindow - VENTANA PRINCIPAL
	-------------------------------------------------------------------------------- */

QMainWindow {
	background: #FFF;
	border: 0px; }

QMainWindow::separator { 
	background: #FFF; 
	border-left: 0px solid #CECECE;
	border-right: 0px solid #CECECE;
	height: 0px;
	width: 0px; }

QMainWindow::separator:hover {
	background: #CECECE; }

