QPushButton:disabled {
	/*background-color: #9E9E9E;*/
                    opacity: 0,8;
	border: 0px solid #9E9E9E;
	color:  #FFF; }

QPushButton::menu-indicator {
	image: none;
	width: 0px; }

/*	--------------------------------------------------------------------------------
	SOLUCIÓN: Nuber PLASTIC
	VERSION: 3.0
	AUTOR: PZS Tecniks
	ULTIMA MODIFICACION: 18 agosto 2020
	-------------------------------------------------------------------------------- */

/*	--------------------------------------------------------------------------------
	QPushButton - BOTÓN
	-------------------------------------------------------------------------------- */

QPushButton {
	background-color: #FFFFFF;
	border: 1px solid #E0E0E0;
	border-radius: 5px;
	color: #212121;
	font-size: 12px;
	height: 60px;
	line-height: 16px;
	text-align: center;
	qproperty-iconSize: 60px; }

QPushButton:hover {
	border: 1px solid #3F51B5; }

QPushButton:focus {
	border: 1px solid #3F51B5; }

QPushButton:pressed {
	border: 1px solid #3F51B5; }

QPushButton:disabled {
                    opacity: 0,8;
	border: 1px solid #9E9E9E;
	color:  #FFF; }

QPushButton::menu-indicator {
	image: none;
	width: 0px; }

/*	--------------------------------------------------------------------------------
	QLabel - ETIQUETA DE TEXTO
	-------------------------------------------------------------------------------- */

QLabel {
	background-color: transparent;
	color: #757575;
	font-size: 15px; }

/*	--------------------------------------------------------------------------------
	SOLUCIÓN: Nuber PLASTIC
	VERSION: 3.0
	AUTOR: PZS Tecniks
	ULTIMA MODIFICACION: 18 agosto 2020

	INDICE
		- QCheckBox				BOTON CHECK
		- QComboBox				COMBOBOX
		- QDateEdit				CAJA DE EDICIÓN DE CAMPO FECHA
		- QDateTime				CAJA DE VISUALIZACIÓN CAMPO FECHA Y HORA
		- QDateTimeEdit			CAJA DE EDICIÓN DE CAMPO FECHA Y HORA
		- QDialog				VENTANA EN CUADRO DE DIÁLOGO
		- QDockWidget			DOCK
		- QDoubleSpinBox		CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
		- QFrame				MARCO
		- QGroupBox				CAJA DE GRUPO
		- QHeaderView   		CABECERA DE REJILLAS Y ÁRBOLES
		- QLabel				ETIQUETA DE TEXTO
		- QLineEdit				CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
		- QMainWindow			VENTANA PRINCIPAL
		- QMenu					MENU CONTEXTUAL
		- QMenuBar				BARRA DE MENU (Solo afecta a Windows)
		- QMessageBox			VENTANA DE MENSAJE
		- QNumberSpinBox		CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
		- QProgressBar			BARRA DE PROGRESO
		- QPushButton			BOTÓN
		- QRadioButton			BOTÓN DE RADIO
		- QScrollBar			BARRA DE SCROLL VERTICAL Y HORIZONTAL
		- QSlider				DESLIZADOR
		- QSpinBox				CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON UN BOTÓN
		- QSplitter				SPLITTER
		- QStatusBar			BARRA DE ESTADO
		- QTabWidget			SEPARADOR DE FORMULARIOS (PESTAÑAS)
		- QTableView			REJILLA
		- QTextEdit				CAJA DE EDICIÓN DE TEXTO MULTI-LINEA
		- QTimeEdit				CAJA DE EDICIÓN DE HORA
		- QToolBar				BARRA DE HERRAMIENTAS
		- QTooltip				TOOLTIP
		- QTreeView				QTREEVIEW - ARBOL VISOR Y MENU ARBOLADO
		- QWidget#qt_calendar	CALENDARIO
		- VBoundFieldEdit		CAJA DE EDICIÓN DE CAMPO PUNTERO A MAESTRO
		- VCFootView			PIE DE REJILLA
		- VListBox				LISTBOX
	-------------------------------------------------------------------------------- */

/*	--------------------------------------------------------------------------------
	QCheckBox - BOTÓN CHECK
	QRadioButton - BOTÓN DE RADIO
	-------------------------------------------------------------------------------- */

QCheckBox, QRadioButton {
	color: #212121; }

QCheckBox:hover, QRadioButton:hover {
	border: 1px solid #757575; }

QCheckBox:focus, QRadioButton:focus {
	border: 2px solid #3F51B5; }

QCheckBox:disabled, QRadioButton:disabled {
	color: #BDBDBD; }

/*	--------------------------------------------------------------------------------
	QComboBox - COMBOBOX
	QDateEdit - CAJA DE EDICIÓN DE CAMPO FECHA
	QDateTime - CAJA DE VISUALIZACIÓN CAMPO FECHA Y HORA
	QDateTimeEdit - CAJA DE EDICIÓN DE CAMPO FECHA Y HORA
	QDoubleSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
	QNumberSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
	QSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON UN BOTÓN
	QTimeEdit - CAJA DE EDICIÓN DE HORA
	VBoundFieldEdit - CAJA DE EDICION DE CAMPO PUNTERO A MAESTRO
	VListBox - LISTBOX
	-------------------------------------------------------------------------------- */

QComboBox, QDateEdit, QDateTime, QDateTimeEdit, QDoubleSpinBox, QNumberSpinBox, QSpinBox, QTimeEdit, VBoundFieldEdit, vBoundFieldEditBrowser, VListBox {
	background-color: #FFFFFF;
	border: 1px solid #E0E0E0;
	color: #212121;
	height: 30px;
	padding-left: 5px;
	padding-right: 0px;
	selection-background-color: #3F51B5;
	selection-color: #FFFFFF; }

QComboBox:hover, QDateEdit:hover, QDateTime:hover, QDateTimeEdit:hover, QDoubleSpinBox:hover, QNumberSpinBox:hover, QSpinBox:hover, QTimeEdit:hover, VBoundFieldEditBrowser:hover,VBoundFieldEdit:hover, VListBox:hover {
	border: 1px solid #757575; }

QComboBox:focus, QDateEdit:focus, QDateTime:focus, QDateTimeEdit:focus, QDoubleSpinBox:focus, QNumberSpinBox:focus, QSpinBox:focus, QTimeEdit:focus, VBoundFieldEditBrowser:focus, VBoundFieldEdit:focus, VListBox:focus {
	border: 2px solid #3F51B5; }

QComboBox:disabled, QDateEdit:disabled, QDateTime:disabled, QDateTimeEdit:disabled, QDoubleSpinBox:disabled, QNumberSpinBox:disabled, QSpinBox:disabled, QTimeEdit:disabled, VBoundFieldEditBrowser:disabled,VBoundFieldEdit:disabled, VListBox:disabled {
	background-color: #BDBDBD; }

QDateEdit::up-button, QDateTime::up-button, QDateTimeEdit::up-button, QTimeEdit::up-button, VBoundFieldEditBrowser::up-button, VBoundFieldEdit::up-button {
	border: none;
	image: url(SENDA_ICONOS_ARR.png); }

QDateEdit::down-button, QDateTime::down-button, QDateTimeEdit::down-button, QTimeEdit::down-button, VBoundFieldEditBrowser::down-button, VBoundFieldEdit::down-button {
	border: none;
	image: url(SENDA_ICONOS_ABA.png); }

QComboBox::drop-down, QDateEdit::drop-down, QDateTime::drop-down, QDateTimeEdit::drop-down, VBoundFieldEditBrowser::drop-down, VBoundFieldEdit::drop-down, QTimeEdit::drop-down {
	border: none;
	image: url(SENDA_ICONOS_ABA.png);
	width: 13px; }

/* EN PRINCIPIO NO AFECTAN */

QNumberSpinBox:up-button, QDoubleSpinBox:up-button, QSpinBox:up-button {
	border: none;
	image: url(SENDA_ICONOS_ARR.png); }

QNumberSpinBox:down-button, QDoubleSpinBox:down-button, QSpinBox:down-button {
	border: none;
	image: url(SENDA_ICONOS_ABA.png); }

/*	--------------------------------------------------------------------------------
	QDockWidget - DOCK
	-------------------------------------------------------------------------------- */

QDockWidget {
	color: #727272;
	font-size: 10px;
	min-width:10px;
	width: 210px; }

QDockWidget::title {
	background: #CECECE;
	margin: 0px;
	padding: 0px;
	text-align: center; }

QDockWidget::close-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px; left: 0px; bottom: 0px;
	width: 0px; }

QDockWidget::float-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px; left: 0px; bottom: 0px;
	width: 0px; }

/*	--------------------------------------------------------------------------------
	QFrame - MARCO
	-------------------------------------------------------------------------------- */

QFrame {
 background: #FFF;
 border: 0px; }

 /*	--------------------------------------------------------------------------------
	QGroupBox - CAJA DE GRUPO
	-------------------------------------------------------------------------------- */

QGroupBox {
	border: 1px solid #E0E0E0;
	border-radius: 5px;
	font-size: 12px;
	font-weight: bold;
	margin-top: 20px;
	padding: 5px; }

QGroupBox::title {
	color: #212121;
	padding-top: -5px;
	padding-left: 5px;
	subcontrol-origin: margin; }

 /*	--------------------------------------------------------------------------------
	QHeaderView - CABECERA DE REJILLAS Y ARBOLES
	-------------------------------------------------------------------------------- */

QHeaderView::section:horizontal {
	background: #FFF;
	border: none;
	border-bottom: 1px solid #BDBDBD;
	border-right: 2px solid #FFF;
	color: #212121;
	font-weight: normal;
	margin-top: 10px;
	min-height: 20px; }

/*	--------------------------------------------------------------------------------
	QLabel - ETIQUETA DE TEXTO
	-------------------------------------------------------------------------------- */

QLabel {
	background-color: transparent;
	color: #757575;
	font-size: 11px; }

/* TITULOS DE LOS FORMULARIOS Y MENUS */

QLabel#TXT_TIT {
	color: #757575;
	font-size: 24px; }

/* TITULOS DEL ADMINISTRADOR DE PROYECTOS */

QLabel#TXT_ADM {
	color: #757575;
	font-size: 18px; }

/*	--------------------------------------------------------------------------------
	QLineEdit - CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
	QTextEdit - CAJA DE EDICIÓN DE TEXTO MULTI-LINEA
	-------------------------------------------------------------------------------- */

QLineEdit, QTextEdit {
	background-color: #FFFFFF;
	border: 1px solid #E0E0E0;
	color: #212121;
	height: 30px;
	padding-left: 2px;
	selection-background-color: #3F51B5;
	selection-color: #FFFFFF; }

QLineEdit:hover, QTextEdit:hover {
	border: 1px solid #757575; }

QLineEdit:focus, QTextEdit:focus {
	border: 2px solid #3F51B5; }

QLineEdit:disabled, QTextEdit:disabled {
	background-color: #BDBDBD; }

/* CONTROL DE EDICIÓN DE TEXTOS A BUSCAR EN MENUS */

QLineEdit#TXT_BUS {
	qproperty-placeholderText: 'Texto a buscar'; }

/* ICONO BÚSQUEDA */
QFrame#BUS_ICO {
	background: transparent;
	color: #FFF; }

/*	--------------------------------------------------------------------------------
	QMainWindow - VENTANA PRINCIPAL
	-------------------------------------------------------------------------------- */

QMainWindow {
	background: #FFF;
	border: 0px; }

QMainWindow::separator { 
	background: #FFF; 
	border-left: 1px solid #CECECE;
	border-right: 1px solid #CECECE;
	height: 5px;
	width: 5px; }

QMainWindow::separator:hover {
	background: #CECECE; }

/*	--------------------------------------------------------------------------------
	QMenu - MENU CONTEXTUAL
	-------------------------------------------------------------------------------- */

QMenu {
	background-color: #FFF;
	border: 1px solid #CECECE;
	color: #333333;
	margin: 0px;
	padding: 5px; }

QMenu::item {
	border: 1px solid transparent;
	margin: 1px;
	padding: 5px 30px 5px 20px; }

QMenu::item:selected {
	background-color: #CECECE;
	color:#000; }

QMenu::separator {
	background-color: #CECECE;
	height: 1px;
	margin: 0px; }

/*	--------------------------------------------------------------------------------
	QMenuBar - BARRA DE MENU (Solo afecta a Windows)
	-------------------------------------------------------------------------------- */

QMenuBar {
	background-color: #FFF;
	border-bottom: 1px solid #CECECE;
	min-height:20px; }

QMenuBar::item:selected {
	background-color: #CECECE;
	color: #000; }

QMenuBar::item:hover { 
	background: #333333;
	color: #FFF; }

/*	--------------------------------------------------------------------------------
	QMessageBox - VENTANA DE MENSAJE
	QDialog - VENTANA EN CUADRO DE DIÁLOGO
	-------------------------------------------------------------------------------- */

QMessageBox, QDialog {
	background-color: #FFF; }

QMessageBox QPushButton {
	min-width: 90px; }

/*	--------------------------------------------------------------------------------
	QProgressBar - BARRA DE PROGRESO
	-------------------------------------------------------------------------------- */

QProgressBar {
	background-color: #FFF;
	border: 1px solid #E0E0E0;
	border-radius: 2px;
	color: #212121;
	height: 30px;
	text-align: center; }

QProgressBar::chunk {
	background-color:#C5CAE9; }

/*	--------------------------------------------------------------------------------
	QPushButton - BOTÓN
	-------------------------------------------------------------------------------- */

QPushButton {
	background-color: #FFFFFF;
	border: 1px solid #E0E0E0;
	border-radius: 5px;
	color: #212121;
	font-size: 12px;
	height: 30px;
	line-height: 16px;
	text-align: center;
	qproperty-iconSize: 18px; }

QPushButton:hover {
	border: 1px solid #3F51B5; }

QPushButton:focus {
	border: 2px solid #3F51B5; }

QPushButton:pressed {
	border: 2px solid #3F51B5; }

QPushButton:disabled {
	background-color: #9E9E9E;
	border: 1px solid #9E9E9E;
	color:  #FFF; }

QPushButton::menu-indicator {
	image: none;
	width: 0px; }

/* BOTÓN ACEPTAR */

QPushButton#BTN_ACE { 
	background-color: #3F51B5;
	color: #FFF; }

QPushButton:hover#BTN_ACE {
	background-color: #FFFFFF;
	color: #3F51B5; }

QPushButton:focus#BTN_ACE {
	background-color: #FFFFFF;
	color: #3F51B5; }

QPushButton:pressed#BTN_ACE {
	background-color: #3F51B5;
	color: #FFF; }

QPushButton:disabled#BTN_ACE {
	color:  #727272; }

/* BOTÓN AMPLIAR, BOTÓN BUSCAR, BOTÓN MENU y BOTÓN REDUCIR */

QPushButton#BTN_AMP, QPushButton#BTN_BUS, QPushButton#BTN_MEN, QPushButton#BTN_RED { 
	background-color: transparent;
	border: 1px solid #E0E0E0;
	border-radius: 5px;
	qproperty-iconSize: 18px; }

QPushButton:hover#BTN_AMP, QPushButton:hover#BTN_BUS, QPushButton:hover#BTN_MEN, QPushButton:hover#BTN_RED {
	border: 1px solid #3F51B5; }

QPushButton:focus#BTN_AMP, QPushButton:focus#BTN_BUS, QPushButton:focus#BTN_MEN, QPushButton:focus#BTN_RED {
	border: 2px solid #3F51B5; }

QPushButton:pressed#BTN_AMP, QPushButton:pressed#BTN_BUS, QPushButton:pressed#BTN_MEN, QPushButton:pressed#BTN_RED {
	border: 2px solid #3F51B5; }

QPushButton:disabled#BTN_AMP, QPushButton:disabled#BTN_BUS, QPushButton:disabled#BTN_MEN, QPushButton:disabled#BTN_RED {
	border: 1px solid #9E9E9E; }

/* BOTÓN CON TAMAÑO FIJO */

QPushButton#BTN_ACE, QPushButton#BTN_CNC, QPushButton#BTN_SUP, QPushButton#BTN_OPC { 
	width: 120px; }

/*	--------------------------------------------------------------------------------
	QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
	-------------------------------------------------------------------------------- */

QScrollBar:vertical {
	background: #FFF;
	border: 1px solid #BDBDBD;
	margin: 20px 0px 20px 0px;
	width: 16px; }

QScrollBar:horizontal {
	background: #FFF;
	border: 1px solid #BDBDBD;
	height: 16px;
	margin: 0px 20px 0px 20px;  }

QScrollBar::handle:vertical, QScrollBar::handle:horizontal {
	background: #BDBDBD;
	border: 1px solid #BDBDBD;
	min-height: 20px; }

QScrollBar::handle:hover {
	background: #757575;
	border: 1px solid #BDBDBD; }

QScrollBar::add-line:vertical {
	background: #FFF;
	border: 1px solid #BDBDBD;
	height: 20px;
	subcontrol-origin: margin;
	subcontrol-position: bottom;
	width: 20px; }

QScrollBar::add-line:horizontal {
	background: #FFF;
	border: 1px solid #BDBDBD;
	height: 20px;
	subcontrol-origin: margin;
	subcontrol-position: right;
	width: 20px; }

QScrollBar::sub-line:vertical {
	background: #FFF;
	border: 1px solid #BDBDBD;
	height: 20px;
	subcontrol-position: top;
	subcontrol-origin: margin;
	width: 20px; }

QScrollBar::sub-line:horizontal {
	background: #FFF;
	border: 1px solid #BDBDBD;
	height: 20px;
	subcontrol-origin: margin;
	subcontrol-position: left;	
	width: 20px; }

QScrollBar::up-arrow:vertical {
	background: #BDBDBD;
	image: url(SENDA_ICONOS_ARR_BLA.png);
	width: 16px; }

QScrollBar::down-arrow:vertical {
	background: #BDBDBD;
	image: url(SENDA_ICONOS_ABA_BLA.png);
	width: 16px; }

QScrollBar:left-arrow:horizontal {
	background: #BDBDBD;
	image: url(SENDA_ICONOS_IZQ_BLA.png);
	height: 16px; }

QScrollBar::right-arrow:horizontal {
	background: #BDBDBD;
	image: url(SENDA_ICONOS_DER_BLA.png);
	height: 16px; }

QScrollBar::up-arrow:vertical:hover, QScrollBar::down-arrow:vertical:hover {
	background: #757575;
	border: 1px solid #BDBDBD; }

QScrollBar::add-page:vertical, QScrollBar::sub-page:vertical, QScrollBar::add-page:horizontal, QScrollBar::sub-page:horizontal {
	background: none; }

/*	--------------------------------------------------------------------------------
	QSlider - DESLIZADOR
	-------------------------------------------------------------------------------- */

QSlider {
	background: #FFF; }

/*	--------------------------------------------------------------------------------
	QSplitter - SPLITTER
	-------------------------------------------------------------------------------- */

QSplitter {
	background: transparent; }

/*	--------------------------------------------------------------------------------
	QStatusBar - BARRA DE ESTADO
	-------------------------------------------------------------------------------- */

QStatusBar {
	background-color: #FFF;
	border-top: 1px solid #CECECE;
	qproperty-visible: false; }

/*	--------------------------------------------------------------------------------
	QTabWidget - SEPARADOR DE FORMULARIOS (Pestañas)
	-------------------------------------------------------------------------------- */

QTabWidget::pane {
	border-top: 1px solid #CECECE;
	margin:0px;
	top: 10px; }

QTabWidget::tab-bar {
	left: 10px;
	min-width: 10px;
	right: 10px;
	top: 10px; }

QTabBar::tab {
	background-color: #FFF;
	border-bottom: 1px solid #FFF;
	border-left: 1px solid #EEEEEE;
	border-top: 1px solid #EEEEEE;
	border-right: 1px solid #EEEEEE;
	color: #212121;
	margin-left: 5px; 
	padding-bottom: 3px;
	padding-left: 5px; 
	padding-right: 5px;
	padding-top: 5px; }

QTabBar::tab:!selected {
	background-color: #EEEEEE;
	border-bottom: 1px solid #EEEEEE;
	color: #424242; }

QTabBar::close-button {
	image: url(SENDA_ICONOS_CRR.png);
	margin: 3px; }

QTabBar::scroller {
	padding-top: 10px;
	width: 30px; }

/*	--------------------------------------------------------------------------------
	QTableView - REJILLA
	VCFootView - PIE DE REJILLA
	-------------------------------------------------------------------------------- */

/* CABECERA DE REJILLA */

QTableView {
	alternate-background-color: transparent;
	background-color: #FFF;
	border: none;
	border: 0px solid #223040;
                    padding:20px;
	font-weight: normal;
	gridline-color: transparent;
	selection-border: 2px solid #223040;
	selection-color: #FFF;
	qproperty-showGrid: false; }

QTableView QTableCornerButton::section {
	background-color: #FFF;
	border: 0px; }

QTableView::item:focus {
	selection-background-color: #55667B;
	selection-color: #FFF; }

/* PIE DE REJILLA */

VCFootView {
	background: #FFF;
	border: none;
	border-right: 2px solid #FFF;
	color: #212121;
	min-height: 20px; }

VCFootView::section:active {
	background: #FFF;
	border: none;
	border-right: 2px solid #FFF; }

VCFootView::section:!active {
	background: #FFF;
	border: none;
	border-right: 2px solid #FFF; }

/*	--------------------------------------------------------------------------------
	QToolBar - BARRA DE HERRAMIENTAS
	-------------------------------------------------------------------------------- */

QToolBar {
	background-color: transparent;
	border: 0px;
	padding: 15px;
	spacing: 10px;
	qproperty-iconSize: 32px; }

QToolButton {
	background-color: #FFF;
	border: 1px solid #E0E0E0;
	border-radius: 23px;
	color: #727272;
	margin-right: 1px;
	min-height: 18px;
	min-width: 18px;
	padding: 5px;
	qproperty-iconSize: 18px; }

QToolButton:hover {
	border: 1px solid #3F51B5;
	border-radius: 23px; }

