	background-color: transparent;
	border: 0px solid #E0E0E0;
	border-radius: 0px;
	color: #FFFFFF;
	margin-right: 0px;
	min-height: 80px;
	min-width: 80px;
	padding: 0px; }

QToolButton:hover {
	background-color: #F2F5F9;
	color: #155F4D; }

QToolButton:focus {
	border: 2px solid #3E5B6F;
	border-radius: 6px; }

QToolButton:disabled {
	background-color: #CECECE;
	border: 1px solid #727272;
	color: #727272; }

QToolButton:pressed {
	border: 2px solid #3E5B6F;
	border-radius: 6px; }

QToolButton::menu-indicator {
	background-color: transparent;
	color: transparent; }

/*	--------------------------------------------------------------------------------
	QDockWidget - DOCK
	-------------------------------------------------------------------------------- */

QDockWidget  {
	color: #7C2325;
	font-size: 10px;
	min-width:10px;
	width: 50px; }

QDockWidget::title {
	background: #7C2325;
	margin: 0px;
	padding: 0px;
	text-align: center; }

QDockWidget::close-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px; left: 0px; bottom: 0px;
	width: 0px; }

QDockWidget::float-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px; left: 0px; bottom: 0px;
	width: 0px; }
